import { useEffect } from 'react';
import { withRouter } from 'react-router-dom';

const ScrollToTopOnPageChange = ({ history }) => {
  useEffect(() => {
    const unlisten = history.listen(() => {
      window.scrollTo(0, 0); // Устанавливаем скролл в начало страницы при изменении маршрута
    });
    return () => {
      unlisten();
    };
  }, [history]);

  return null;
};

export default ScrollToTopOnPageChange;
